import { FC, Fragment, useEffect, useState } from 'react';
import { Button, Image, Card, Row, Col, Spin } from 'antd';
import { get } from '@/common/request/index';
import { BaseHeatData, HeapMapProps } from '@/views/analysis/pages/index/ts-config';
import cs from 'classnames';
import styles from '../index.module.less';

interface ShowImageProps {
  image?: string | undefined;
  bgImg: string | undefined;
}

const ShowHeadMapImage: FC<ShowImageProps> = ({ bgImg, image }) => {
  return (
    <Col span={12}>
      <Card>
        <div className={cs(styles.layerMask, !image && styles.errorMask)}>
          {bgImg ? (
            <>
              <img src={bgImg} alt='' />
              {image && <img src={image} alt='' />}
            </>
          ) : (
            <Image
              src={'error'}
              fallback='data:image/png;base64,'
            />
          )}
        </div>
      </Card>
    </Col>
  );
};

const HeatmapContainer: FC<HeapMapProps> = ({ deviceId, filters }) => {
  const { storeIds, start, end } = filters;
  const [loading, setLoading] = useState<boolean>(false);
  const [show, setShow] = useState<boolean>(false);
  const [headMapData, setHeadMapData] = useState<BaseHeatData>({ name: '', bgImg: '', heatmap: '' });

  useEffect(() => {
    // 筛选条件变动时，重置热力图，不再显示之前加载的热力图
    setShow(false);
  }, [filters]);

  // 获取热力图
  const getHeatmap = async () => {
    setLoading(true);
    const params = { start, end, storeId: storeIds, deviceId };
    try {
      // https://yapi.lanhanba.com/project/297/interface/api/33360
      const data: BaseHeatData = await get('/store/heatmaps', params);
      setHeadMapData(data);
      setShow(true);
    } catch (error) {}
    setLoading(false);
  };

  const showHeadMap = () => {
    getHeatmap();
  };

  const CardOperate = (
    <Col span={12}>
      <Card>
        <Spin spinning={loading}>
          <div className={styles.layerMask}>
            {!loading && (
              <Button type='primary' onClick={showHeadMap}>
                查看热力图
              </Button>
            )}
          </div>
        </Spin>
      </Card>
    </Col>
  );

  return (
    <div className={styles.heatmapContainer}>
      <Row gutter={[24, 0]}>
        {show ? (
          <Fragment>
            <ShowHeadMapImage image={headMapData?.heatmap} bgImg={headMapData?.bgImg} />
            <ShowHeadMapImage bgImg={headMapData?.bgImg} />
          </Fragment>
        ) : (
          <Fragment>
            {CardOperate}
            {CardOperate}
          </Fragment>
        )}
      </Row>
    </div>
  );
};

export default HeatmapContainer;
